import React, { useState, useContext } from 'react';

import './booking.css';
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from 'reactstrap';
import { useNavigate } from 'react-router-dom';
import {AuthContext} from "../../context/AuthContext"
import {BASE_URL} from "../../utils/config"
const Booking = ({ tour, avgRating }) => {
  const { price, reviews, title } = tour;
  const navigate = useNavigate();
 const {user} = useContext(AuthContext)
  const [booking, setBooking] = useState({
    userId: user && user._id,
    userEmail: user && user.email,
    tourName:title,
    fullName: '',
    phone: '',
    guestSize: 1,
    bookAt: '',
  });

  const handleChange = (e) => {
    setBooking((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const serviceFee = 10;
  const totalAmount = Number(price) * Number(booking.guestSize) + Number(serviceFee);

  // send data to the server
  const handleClick = async e => {
    e.preventDefault();

try{
  if(!user || user===undefined || user===null){
    return alert('please sign in')
  }

  const res = await fetch(`${BASE_URL}/review`,{
    method:'post',
    header:{
      'content-type':'application/json'
    },
    credentials:'include',
    body:JSON.stringify(booking)
  })
const result = await res.json()
if(!res.ok){
  return alert(result.message)
}
navigate("/thank-you")
}catch(err){

  alert(err.message)

}



    // Send credentials data to the server here.
    // Assuming there's a function named "sendDataToServer" that handles this.
    //sendDataToServer(credentials);
    navigate('/thank-you');
  };

  return (
    <div className="booking">
      <div className="booking_top d-flex align-items-center justify-content-between">
        <h3>
          ${price}
          <span>/per person</span>
        </h3>
        <span className="tour_rating d-flex align-items-center">
          <i className="ri-star-s-fill"></i>
          {avgRating === 0 ? null : avgRating} ({reviews?.length})
        </span>
      </div>
      {/* -----------------BOOKING FORM-----------*/}
      <div className="booking_form">
        <h5>Information</h5>
        <Form className="booking_info-form" onSubmit={handleClick}>
          <FormGroup>
            <input
              type="text"
              placeholder=" Full Name "
              id="fullName"
              required
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="number"
              placeholder=" Phone "
              id="phone"
              required
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup className="d-flex align-items-centre gap-3">
            <input
              type="date"
              placeholder=""
              id="bookAt"
              min={new Date().toISOString().split('T')[0]} // Restrict past dates
              required
              onChange={handleChange}
            />
            <input
              type="number"
              placeholder=" Guest "
              id="guestSize"
              required
              onChange={handleChange}
            />
          </FormGroup>
        </Form>
      </div>
      {/* -----------------BOOKING END-----------*/}
      {/* -----------------BOOKING BOTTOM-----------*/}
      <div className="booking_bottom">
        <ListGroup>
          <ListGroupItem className="border-0 px-0">
            <h5 className="d-flex align-items-center gap-1">
              ${price} <i className="ri-close-line"></i> 1 person
            </h5>
            <span>${price}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0">
            <h5>Service charge</h5>
            <span>${serviceFee}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0 total">
            <h5>Total</h5>
            <span>${totalAmount}</span>
          </ListGroupItem>
        </ListGroup>
        <Button className="btn primary_btn w-100 mt-4" onClick={handleClick}>
          Book Now
        </Button>
      </div>
    </div>
  );
};

export default Booking;
